import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import './Expenses.css';
import Card from "../UI/Card";
import {useState} from 'react'


function Expenses(props){
    const expenses = props.item;

    const [filteredYear, setFilteredYear] = useState('2021');

    const filterHandler = (selectedYear)=>{
        setFilteredYear(selectedYear);
        console.log(filteredYear);
    }

    const filteredExpenses = expenses.filter(expense => {
        const year = expense.date.getFullYear().toString() === filteredYear
        return year ;
    });

    let expensesContent = <p>Nothing to show</p>
    if (filteredExpenses.length){
                 expensesContent= filteredExpenses.map(
                    expense =>
                        <ExpenseItem  key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}>  </ExpenseItem>
                )

    }



    return (
        <>
        <Card className="expenses">
            <ExpensesFilter onChangeFilter={filterHandler} selected={filteredYear}></ExpensesFilter>
            {expensesContent}
        </Card>
        </>
    )
}
export default Expenses;
